            </div>

            <br/><br/>

            <div className="logo">
                <div ><HomeCard logo={Computer} title="Coding"/></div>
                <div><HomeCard logo={Music} title="Music"/></div>
            </div>
            </>
        );
    }
}

export default Homepage;
