import React from 'react';
import {Redirect} from 'react-router-dom';
import LucasCard from '../components/LucasCard';
import '../styles/Music.css';
import Hundred from "../assets/hundred.png";
import Synth from "../assets/synth.png";

class Music extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            redirectLink: "/music"
        };
    }

    handleClick(page) {
        const link = this.state.redirectLink.concat(page);
        this.setState({redirectLink: link})
        console.log(this.state.redirectLink);
    }

    render() {
        const cardInfos = [
            {link:"/cssquared", logo:Computer, title:"CSCS: Computer Science Course Selector"},
            {link:"/rshinyapps", logo:Math, title:"Math Visualization Apps in RShiny"},
            {link:"/wavelearning", logo:Data, title:"Wave Learning Festival: Data Analysis and Web Apps"},
            {link:"/votrcrowd", logo:IvyHacks, title:"VotrCrowd: IvyHacks Hackathon"},
            {link:"/polysymph", logo:Synthesizer, title:"PolySymph Synthesizer"},
            {link:"/datamatch", logo:Datamatch, title:"Harvard Datamatch"}
        ]

        const cards = cardInfos.map((card) =>
            <div onClick={() => this.handleClick(card.link)}>
                <LucasCard logo={card.logo} title={card.title} styleClass="card"/>
            </div>
        );

        if(this.state.redirectLink !== "/music") {
            return <Redirect to={this.state.redirectLink}/>
        }

        return (
            <div>
                <h1>ðŸŽµ Music ðŸŽµ</h1>
                <hr/><br/>
                <h3>I am also enrolled in a special dual-degree program with the Berklee College of Music, where I intend to get a Masters in Music in Contemporary Performance and Produciton.</h3>
                <div>Check out some of my projects by clicking one of the icons below!</div>

                <br/><br/><br/>

                <div className="cards">
                    <div onClick={() => this.handleClick("/100daysongchallenge")}><LucasCard logo={Hundred} title="100 Day Song Challenge"/></div>
                    <div onClick={() => this.handleClick("/composition")}><LucasCard logo={Synth} title="Composition"/></div>
                </div>
            </div>

        );
    }
}

export default Music;
