import React from 'react';
import {Redirect} from 'react-router-dom';
import './Homepage.css';
import LucasCard from './LucasCard';
import Computer from "../assets/computer.png";
import Music from "../assets/music.png";
import Footer from "./Footer";

class Homepage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            redirectLink: "/"
        };
    }

    handleClick(page) {
        const link = this.state.redirectLink.concat(page);
        this.setState({redirectLink: link})
    }

    render() {
        if(this.state.redirectLink !== "/") {
            return <Redirect to={this.state.redirectLink}/>
        }
        return (
            <>
            <h1>ðŸ‘‹ Welcome! ðŸ‘‹</h1><hr/><br/>
            <div>
                <h2>My name is Lucas Pao, and I like to create.</h2>
                Check out some of my projects by clicking one of the emojis below!
            </div>

            <br/><br/><br/>

            <div className="cards">
                <div onClick={() => this.handleClick("coding")}><LucasCard logo={Computer} title="Coding"/></div>
                <div onClick={() => this.handleClick("music")}><LucasCard logo={Music} title="Music"/></div>
            </div>
            <br/><hr/><br/>
            </>
        );
    }
}

export default Homepage;
